import React, { useState } from "react";
import "./Goals.module.css";
import Goal from "./Goals";
const Goals = (props) => {
  const [goals, setGoals] = useState([]);
  const addGoalHandler = (goal) => {
    if (goal.name.trim().length > 0) {
      setGoals((prevGoals) => {
        return [...prevGoals, goal];
      });
    }
  };

  return (
    <div className={(props.className = " goals")} onAddGoal={addGoalHandler}>
      {goals.map((goal) => {
        return <Goal name={goal.name} age={goal.age}></Goal>;
      })}
    </div>
  );
};

export default Goals;
