import React, { useState } from "react";
import styles from "./AddUser.module.css";
import FormControl from "../FormControl/FormControl";
import Container from "../UI/Container";
import Button from "../UI/Button";
import ErrorModal from "../ErrorModal/ErrorModal";

const AddUser = (props) => {
  const [username, setUsername] = useState("");
  const [age, setAge] = useState("");
  const [isInputValid, setIsInputValid] = useState(true);
  const [errorObj, setErrorObj] = useState({ title: "", message: "" });

  const usernameChangeHandler = (e) => {
    e.preventDefault();
    if (e.target.value.trim().length > 0) {
      setUsername(e.target.value.trim());
    }
  };

  const ageChangeHandler = (e) => {
    e.preventDefault();
    if (+e.target.value > 0) {
      setAge(e.target.value);
    }
  };

  const addUserHandler = (e) => {
    e.preventDefault();
    if (username?.trim()?.length > 0 && +age > 0) {
      console.log("Adding user");
      props.onAddUserToList({ name: username, age: age });
      setAge("");
      setUsername("");
    } else {
      if (username?.trim()?.length > 0 && +age <= 0) {
        setErrorObj({
            title: "Invalid Age",
            message: "User"
        });
      } else if (username?.trim()?.length === 0 && +age > 0) {
        setErrorObj({
            title: "Empty Username",
            message: "Username cannot be null"
        });
      }
      setIsInputValid(false);
    }
  };

  const errorModalClickHandler = (e) => {
    e.preventDefault();
    setIsInputValid(true);
  };

  return (
    <Container id={props.id}>
      {isInputValid && (
        <form>
          <FormControl>
            <label className={styles.label}>Username</label>
            <input
              type="text"
              className={styles.input}
              placeholder="Username"
              onChange={usernameChangeHandler}
              value={username}
            />
          </FormControl>
          <FormControl className={styles["form-control"]}>
            <label className={styles.label}>Age (Years)</label>
            <input
              type="number"
              className={styles.input}
              placeholder="Age"
              onChange={ageChangeHandler}
              value={age}
            />
          </FormControl>
          <FormControl>
            <Button
              type="button"
              onClick={addUserHandler}
              className={styles.button}
            >
              Add User
            </Button>
          </FormControl>
        </form>
      )}
      {!isInputValid && (
        <ErrorModal
          title={errorObj.title}
          message={errorObj.message}
          onButtonClick={errorModalClickHandler}
        ></ErrorModal>
      )}
    </Container>
  );
};

export default AddUser;
