import React, { useState } from "react";
import "./Goals.module.css";
import Goal from "../Goal/Goal";
const Goals = (props) => {
  const [goals, setGoals] = useState([props.Goals]);
  const addGoalHandler = (goal) => {
    if (goal?.name?.trim()?.length > 0) {
      setGoals((prevGoals) => {
        return [...prevGoals, goal];
      });
    }
  };

  return (
    <div id={props.id} className={props.className} onAddGoal={addGoalHandler}>
      {goals.map((goal) => {
        if (goal?.name?.trim().length > 0) {
          return <Goal name={goal?.name} age={goal?.age} />;
        } else {
          return "";
        }
      })}
    </div>
  );
};

export default Goals;
