import React, { useState } from "react";
import "./App.css";
import AddUser from "./components/AddUser/AddUser";
import Container from "./components/UI/Container";
import Goals from "./components/Goals/Goals";

const App = () => {
  const [goalsList, SetGoalsList] = useState([]);

  const addToGoalsListHandler = (goal) => {
    SetGoalsList((prevGoals) => {
      return [...prevGoals, { name: goal.name, age: goal.age }];
    });
  };

  return (
    <Container>
      <Container>
        <AddUser
          id="goal-form"
          onAddGoalToList={addToGoalsListHandler}
        />
      </Container>
      <Container>
        <Goals id="goals" Goals={goalsList}></Goals>
      </Container>
    </Container>
  );
};

export default App;
